mapper.select:
SELECT I.id, IFNULL(I.title,'') title, IFNULL(I.brief,'') brief, IFNULL(I.image,'') image, IFNULL(I.source,'') source, I.create_time FROM `INFORMATION` I LEFT JOIN `information_channel` ic ON I.information_channel_id = ic.id WHERE find_in_set(#{channelId}, information_channel_id ) AND I.`status` =1
AND top =#{top} ORDER BY I.top_time DESC LIMIT #{num}
ORDER BY I.create_time DESC

SELECT id, IFNULL(title,'') title, IFNULL(brief,'') brief, IFNULL(image,'') image, IFNULL(source,'') source, create_time FROM information WHERE find_in_set(#{channelId}, information_channel_id ) AND `status` =1
AND top =#{top} ORDER BY top_time DESC LIMIT #{num}
ORDER BY create_time DESC

mapper.update:
UPDATE information_collection ic SET ic.`status` =-1
AND ic.information_id
#{id}
AND ic.creator_id =#{userId}
